<script lang="ts">
import Vue from "vue";
import Component from "vue-class-component";
import gsap from "gsap";
import { ScrollTrigger } from "gsap/ScrollTrigger";
import DeviceHelper from "@/helpers/DeviceHelper.ts";

@Component
export default class ProductSummaryClipart extends Vue {
  /** Returns true if user device is a phone */
  get isPhone(): boolean {
    return DeviceHelper.isPhone();
  }

  /** Returns true if user device is a tablet */
  get isTablet(): boolean {
    return DeviceHelper.isTablet();
  }

  /** Returns the device height in pixels */
  get screenHeight(): number {
    return DeviceHelper.getHeight();
  }

  mounted() {
    if (this.isTablet) {
      return;
    }

    gsap.registerPlugin(ScrollTrigger);

    // scroll trigger animations for clipart
    const scrollTriggerConfig = {
      trigger: "#product-summary>.main-text-container",
      endTrigger: "#setting-up>.main-text-container",
      start: "bottom center",
      end: `center center+=90px`,
      scrub: this.isPhone ? false : 0.5,
      pin: this.isPhone ? undefined : ".graph-clipart",
      toggleActions: "play none none none"
    };
    const timeline = gsap.timeline({
      scrollTrigger: scrollTriggerConfig,
      duration: 1.5
    });
    timeline
      .to(
        "#animation-svg",
        {
          scale: this.isPhone ? 1.3 : 1,
          transformOrigin: "50% 25%"
        },
        0
      )
      .to(
        ".graph-clipart",
        {
          scale: this.isPhone ? 1 : 0.8
        },
        0
      )
      .from(
        "#graph",
        {
          y: -270
        },
        0
      )
      .to(
        "#graph",
        {
          scale: 0.5,
          transformOrigin: "50% 50%"
        },
        0
      )
      .from(
        "#screenshot",
        {
          opacity: 0
        },
        0
      )
      .from(
        "#phone-shadow",
        {
          opacity: 0
        },
        "-=0.3"
      )
      .to(
        "#phone-shadow",
        {
          scale: 0.95,
          transformOrigin: "50% 50%"
        },
        "-=0.6"
      )
      .from(
        "#map",
        {
          opacity: 0,
          y: 20
        },
        "-=0.3"
      )
      .from("#doughnut", {
        opacity: 0,
        y: 20
      });
  }
}
</script>
